document.getElementById('testPing').addEventListener('click', () => {
    testMessage('PING');
});

document.getElementById('testScan').addEventListener('click', () => {
    testMessage('START_SCAN');
});

document.getElementById('testAPI').addEventListener('click', () => {
    testMessage('TEST_CHROME_API');
});

function testMessage(action) {
    const resultDiv = document.getElementById('result');
    resultDiv.textContent = `Testing ${action}...`;
    
    chrome.runtime.sendMessage({action: action}, (response) => {
        if (chrome.runtime.lastError) {
            resultDiv.textContent = `Error: ${chrome.runtime.lastError.message}`;
        } else {
            resultDiv.textContent = `Success: ${JSON.stringify(response, null, 2)}`;
        }
    });
} 